import React, { useState, useEffect, useRef } from 'react';
import io from 'socket.io-client';
import EmojiPicker from 'emoji-picker-react';

function App() {
  const [socket, setSocket] = useState(null);
  const [username, setUsername] = useState('');
  const [message, setMessage] = useState('');
  const [messages, setMessages] = useState([]);
  const [isConnected, setIsConnected] = useState(false);
  const [typingUsers, setTypingUsers] = useState([]);
  const [isJoined, setIsJoined] = useState(false);
  const [showEmojiPicker, setShowEmojiPicker] = useState(false);
  const [room, setRoom] = useState('general');
  const messageEndRef = useRef(null);
  const emojiButtonRef = useRef(null);

  useEffect(() => {
    // Initialize socket connection
    const newSocket = io('http://localhost:5000');
    setSocket(newSocket);

    // Handle connection events
    newSocket.on('connect', () => {
      setIsConnected(true);
      console.log('Connected to server');
    });

    newSocket.on('disconnect', () => {
      setIsConnected(false);
      console.log('Disconnected from server');
    });

    // Listen for messages
    newSocket.on('receive_message', (data) => {
      setMessages(prev => [...prev, data]);
    });

    // Listen for user join/leave events
    newSocket.on('user_joined', (data) => {
      setMessages(prev => [...prev, data]);
    });

    newSocket.on('user_left', (data) => {
      setMessages(prev => [...prev, data]);
    });

    // Listen for message reactions
    newSocket.on('reaction_added', (data) => {
      setMessages(prev => prev.map(msg => {
        if (msg.id === data.messageId) {
          return {
            ...msg,
            reactions: {
              ...msg.reactions,
              [data.reaction]: (msg.reactions?.[data.reaction] || 0) + 1
            }
          };
        }
        return msg;
      }));
    });

    // Listen for typing events
    newSocket.on('user_typing', (data) => {
      if (data.isTyping) {
        setTypingUsers(prev => {
          if (!prev.includes(data.username)) {
            return [...prev, data.username];
          }
          return prev;
        });
      } else {
        setTypingUsers(prev => prev.filter(user => user !== data.username));
      }
    });

    // Load message history
    if (isJoined) {
      fetch(`http://localhost:5000/messages?room=${room}`)
        .then(response => response.json())
        .then(data => {
          setMessages(data);
        })
        .catch(error => {
          console.error('Error loading message history:', error);
        });
    }

    // Close emoji picker when clicking outside
    const handleClickOutside = (event) => {
      if (emojiButtonRef.current && !emojiButtonRef.current.contains(event.target)) {
        setShowEmojiPicker(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      newSocket.close();
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, [isJoined, room]);

  // Scroll to bottom when new message arrives
  useEffect(() => {
    messageEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  const handleJoin = (e) => {
    e.preventDefault();
    if (username.trim() && socket) {
      socket.emit('user_join', { 
        username: username.trim(),
        room: room
      });
      setIsJoined(true);
    }
  };

  const handleSendMessage = (e) => {
    e.preventDefault();
    if (message.trim() && socket) {
      socket.emit('send_message', { message: message.trim() });
      setMessage('');
      setShowEmojiPicker(false);
      
      // Notify server that user stopped typing
      socket.emit('typing', { isTyping: false });
    }
  };

  const handleTyping = (e) => {
    setMessage(e.target.value);
    
    if (socket) {
      if (e.target.value.trim()) {
        socket.emit('typing', { isTyping: true });
      } else {
        socket.emit('typing', { isTyping: false });
      }
    }
  };

  const handleEmojiClick = (emojiData) => {
    setMessage(prevMessage => prevMessage + emojiData.emoji);
    setShowEmojiPicker(false);
  };

  const handleReaction = (messageId, reaction) => {
    if (socket) {
      socket.emit('message_reaction', { messageId, reaction });
    }
  };

  const formatTime = (timestamp) => {
    const date = new Date(timestamp);
    return date.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
  };

  if (!isConnected) {
    return (
      <div className="container">
        <div className="connection-status">Connecting to server...</div>
      </div>
    );
  }

  if (!isJoined) {
    return (
      <div className="container">
        <div className="login-form">
          <h1>Join Chat Room</h1>
          <form onSubmit={handleJoin}>
            <input
              type="text"
              placeholder="Enter your username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              required
              autoFocus
            />
            <select 
              value={room} 
              onChange={(e) => setRoom(e.target.value)}
              className="room-select"
            >
              <option value="general">General</option>
              <option value="random">Random</option>
              <option value="tech">Tech</option>
            </select>
            <button type="submit">Join Chat</button>
          </form>
        </div>
      </div>
    );
  }

  return (
    <div className="container">
      <div className="chat-container">
        <div className="chat-header">
          <h2>Real-Time Chat - {room}</h2>
          <div className="status">
            <span className={`status-indicator ${isConnected ? 'connected' : 'disconnected'}`}></span>
            {isConnected ? 'Connected' : 'Disconnected'}
          </div>
        </div>

        <div className="messages-container">
          {messages.map((msg, index) => {
            const isMyMessage = msg.username === username;
            const isSystemMessage = !msg.username || msg.message.includes('joined') || msg.message.includes('left');
            
            let messageClass = 'other-message';
            if (isMyMessage) messageClass = 'my-message';
            if (isSystemMessage) messageClass = 'system-message';
            
            return (
              <div key={index} className={`message ${messageClass}`}>
                {!isSystemMessage && (
                  <div className="message-header">
                    <span className="username">{msg.username}</span>
                    <span className="timestamp">{formatTime(msg.timestamp)}</span>
                  </div>
                )}
                <div className="message-content">{msg.message}</div>
                
                {/* Message Reactions */}
                {!isSystemMessage && (
                  <div className="message-reactions">
                    {Object.entries(msg.reactions || {}).map(([reaction, count]) => (
                      <span 
                        key={reaction} 
                        className="reaction"
                        onClick={() => handleReaction(msg.id, reaction)}
                      >
                        {reaction} {count}
                      </span>
                    ))}
                    <div className="reaction-options">
                      <button onClick={() => handleReaction(msg.id, 'üëç')}>üëç</button>
                      <button onClick={() => handleReaction(msg.id, '‚ù§Ô∏è')}>‚ù§Ô∏è</button>
                      <button onClick={() => handleReaction(msg.id, 'üòÇ')}>üòÇ</button>
                      <button onClick={() => handleReaction(msg.id, 'üòÆ')}>üòÆ</button>
                    </div>
                  </div>
                )}
              </div>
            );
          })}
          <div ref={messageEndRef} />
        </div>

        <div className="typing-indicator">
          {typingUsers.length > 0 && (
            <p>
              {typingUsers.join(', ')} {typingUsers.length === 1 ? 'is' : 'are'} typing
              <span></span><span></span><span></span>
            </p>
          )}
        </div>

        <form className="message-form" onSubmit={handleSendMessage}>
          <div className="emoji-picker-container">
            <button 
              type="button" 
              className="emoji-button"
              onClick={() => setShowEmojiPicker(!showEmojiPicker)}
              ref={emojiButtonRef}
            >
              üòä
            </button>
            {showEmojiPicker && (
              <div className="emoji-picker">
                <EmojiPicker onEmojiClick={handleEmojiClick} />
              </div>
            )}
          </div>
          <input
            type="text"
            placeholder="Type your message..."
            value={message}
            onChange={handleTyping}
            required
          />
          <button type="submit">Send</button>
        </form>
      </div>
    </div>
  );
}

export default App;
